import unittest

from cozy.target_syntax import *
from cozy.syntax_tools import pprint
from cozy.handle_tools import fix_ewithalteredvalue
from cozy.solver import satisfy
from cozy.evaluation import eval

class TestSyntaxTools(unittest.TestCase):

    def check_ewithalteredvalue(self, e1):
        e2 = fix_ewithalteredvalue(e1)
        m = satisfy(ENot(EBinOp(e1, "===", e2).with_type(BOOL)))
        if m is not None:
            print("INVALID TRANSFORM")
            print(" --> e  = {}\n".format(pprint(e1)))
            print(" --> e' = {}\n".format(pprint(e2)))
            print(" --> m  = {}\n".format(repr(m)))
            print(" --> e[m]  = {}".format(eval(e1, m)))
            print(" --> e'[m] = {}".format(eval(e2, m)))

